using System;
using System.Collections.Generic;
using System.Linq;
using System.Threading.Tasks;
using trangtin.Models;

namespace trangtin.Data
{
    public class SeedData
    {
		// Data initialize
		public static void Initialize(ApplicationDbContext context)
		{
			context.Database.EnsureCreated();

			// Kiểm tra DB đã có dữ liệu chưa
			if (context.chuyenmuc.Any() || context.chude.Any() || context.bantin.Any())
			{
				return;
			}

			var dschuyenmuc = new chuyenmuc[]
			{
				new chuyenmuc{tenchuyenmuc="Thời sự",thutu=1},
				new chuyenmuc{tenchuyenmuc="Giáo dục",thutu=2},
				new chuyenmuc{tenchuyenmuc="Thể thao",thutu=3},
				new chuyenmuc{tenchuyenmuc="Thời trang",thutu=4}
			};

			context.chuyenmuc.AddRange(dschuyenmuc);
			context.SaveChanges();

			var dschude = new chude[]
			{
				new chude{tenchude="Xu hướng",chuyenmucid=4,thutu=0,kichhoat=1},
				new chude{tenchude="Bóng đá",chuyenmucid=3,thutu=0,kichhoat=1},
				new chude{tenchude="Tuyển sinh",chuyenmucid=2,thutu=0,kichhoat=1},
				new chude{tenchude="Giáo dục 4.0",chuyenmucid=2,thutu=0,kichhoat=1},
				new chude{tenchude="Tennis",chuyenmucid=3,thutu=0,kichhoat=1},
				new chude{tenchude="Chính trị",chuyenmucid=1,thutu=0,kichhoat=1},
				new chude{tenchude="Thế giới",chuyenmucid=4,thutu=0,kichhoat=1}
			};

			context.chude.AddRange(dschude);
			context.SaveChanges();

			var dsbantin = new bantin[]
			{
				new bantin{chudeid=7,tieude="Làn sóng Covid-19 mới đe dọa đà phục hồi của châu Âu",tomtat="Đà phục hồi kinh tế mong manh của châu Âu bị đe dọa bởi làn sóng bùng phát dịch lần thứ tư đang hoành hành khắp châu lục. Các chính phủ áp đặt hạn chế ngày càng nghiêm ngặt. Du lịch không được khuyến khích. Các trung tâm mua sắm, quán bar, nhà hàng và khu trượt tuyết nghỉ dưỡng cũng phải giảm lượng khách.",noidung="Áo áp đặt các biện pháp nghiêm ngặt nhất, bắt buộc tiêm phòng và đóng cửa nhiều hoạt động trên toàn quốc từ thứ Hai (22/11). Ở Bỉ, người có hộ chiếu vaccine đến từ Pháp và Thụy Sĩ sẽ phải làm việc tại nhà 4 ngày mỗi tuần. 'Chúng ta đang tiến đến một mùa đông khó khăn. Đại dịch dường như đang ảnh hưởng tiêu cực đến nền kinh tế nhiều hơn những gì chúng ta nghĩ ban đầu', Stefan Kooths, Giám đốc nghiên cứu của Viện Kinh tế Thế giới Kiel (Đức) đánh giá. Các đợt đóng cửa đầu đại dịch năm ngoái đã khiến GDP châu Âu giảm gần 15%. Được sự hỗ trợ của chính phủ, hầu hết quốc gia đã xoay sở để phục hồi sau khi vaccine được phủ rộng, tỷ lệ lây nhiễm giảm và các hạn chế được nới lỏng. Hồi tháng 9, các nhà kinh tế lạc quan tuyên bố rằng châu Âu đã đến bước ngoặt. Nhưng những tuần gần đây, các mối đe dọa với đà phục hồi đã quay lại, với việc tái bùng phát dịch dẫn đến tắc nghẽn chuỗi cung ứng, tăng giá năng lượng và lạm phát. Tiêm chủng được kỳ vọng sẽ giúp mọi người có thể tiếp tục tự do mua sắm, ăn uống và du lịch. Nhưng giờ một số chính phủ phải ban bố các hạn chế cứng rắn hơn. Nguyên nhân là một biến thể rất dễ lây lan và phong trào chống đeo khẩu trang đã khiến virus tái bùng phát ở một số khu vực. 'Tỷ lệ tiêm chủng càng thấp thì triển vọng kinh tế càng ảm đạm trong mùa đông năm nay', Kooths nói. Khoảng hai phần ba dân số châu Âu đã được tiêm phòng, nhưng tỷ lệ này rất khác nhau giữa các quốc gia. Chẳng hạn, chỉ một phần tư dân số ở Bulgaria đã tiêm vaccine, so với 81% ở Bồ Đào Nha, theo Trung tâm Phòng ngừa và Kiểm soát Dịch bệnh châu Âu.",ngaydang=DateTime.Now,nguoidang="Quản trị",hinhanh="h1.jpg",tieudehinh="Chợ Giáng sinh ở Frankfurt, Đức hôm 22/11.",luotxem=0,noibat=true,kichhoat=true},
				new bantin{chudeid=7,tieude="Thụy Điển có nữ Thủ tướng đầu tiên",tomtat="Trong cuộc bỏ phiếu tại quốc hội Thụy Điển với tổng cộng 349 ghế hôm nay, 117 nghị sĩ ủng hộ bà Andersson, 174 nghị sĩ phản đối, trong khi 57 nghị sĩ bỏ phiếu trắng và một nghị sĩ vắng mặt.",noidung="Quốc hội Thụy Điển phê chuẩn Bộ trưởng Tài chính Magdalena Andersson trở thành nữ Thủ tướng đầu tiên trong lịch sử đất nước. Theo hiến pháp Thụy Điển, thủ tướng vẫn có thể được phê chuẩn và điều hành đất nước, miễn là đa số thành viên quốc hội, tối thiểu 175 nghị sĩ, không chống lại họ. Quyết định bầu bà Andersson, 54 tuổi, làm Thủ tướng đánh dấu mốc quan trọng đối với Thụy Điển, quốc gia luôn được đánh giá là một trong những nước tiến bộ nhất châu Âu về bình đẳng giới, song chưa phụ nữ nào giữ vai trò thủ tướng. Thủ tướng đương nhiệm Stefan Lofven vẫn điều hành đất nước cho đến khi chính phủ mới của bà Andersson dự kiến ra mắt vào ngày 26/11. Andersson dự định thành lập chính phủ liên minh gồm đảng Dân chủ Xã hội của bà và đảng Xanh. Bà Andersson đang tìm cách đảm bảo sự ủng hộ của hai đảng nhỏ hơn là đảng Cánh tả và đảng Trung tâm. Cả hai đảng này đều không bỏ phiếu chống lại bà tại quốc hội hôm nay.",ngaydang=DateTime.Parse("29/09/2022"),nguoidang="Quản trị",hinhanh="h2.jpg",tieudehinh="Bà Magdalena Andersson phát biểu tại đại hội đảng Dân chủ Xã hội ở Gothenburg, Thụy Điển, hôm 4/11.",luotxem=0,noibat=true,kichhoat=true},
				new bantin{chudeid=2,tieude="Vì sao cầu thủ Chelsea không bị phạt dù bóng chạm tay?",tomtat="Dù Antonio Rudiger để bóng chạm tay rõ ràng, trọng tài vẫn công nhận bàn thắng được ghi sau đó của Trevor Chalobah, trong trận thắng Juventus 4-0 ở Champions League.",noidung="Từ quả phạt góc phút 25, bóng qua đầu tiền vệ của Juventus Adrien Rabiot, bật lên trúng tay hậu vệ chủ nhà Antonio Rudiger rồi văng đến chỗ Trevoh Chalobah. Trung vệ của Chelsea nhanh chân sút bồi tung lưới thủ môn Wojciech Szczesny, mở tỷ số cho chủ sân Stamford Bridge. Theo luật cũ, trọng tài có quyền thổi phạt bất kỳ hành động chạm tay nào từ một cầu thủ tấn công trong lúc kiến tạo bàn thắng, bất kể cố ý hay không. Tuy nhiên, Ủy ban Bóng đá Quốc tế IFAB, cơ quan quản lý luật bóng đá, đã thay đổi luật này từ hè 2021. Theo đó, trọng tài chỉ thổi phạt nếu cầu thủ ghi bàn để bóng chạm tay, còn cầu thủ tấn công khác để bóng chạm tay, lỗi cố ý sẽ được xem xét theo cách thông thường. Trong trường hợp của Chelsea, bóng trúng tay Rudiger từ một pha phá bóng không thể đoán định của Rabiot. Trung vệ chủ nhà để tay trước ngực, có nghĩa nếu bóng không đập vào tay thì cũng đập vào cơ thể. Do đó, trọng tài không bắt lỗi, bất chấp phản ứng của cầu thủ Juventus. Bàn của Chalobah giúp Chelsea chiếm lợi thế trong hiệp một, rồi ghi thêm ba bàn nhờ công Reece James, Hudson-Odoi và Timo Werner trong hiệp hai. Thắng 4-0 tối 23/11, thầy trò Thomas Tuchel trả món nợ thua 0-1 trong trận lượt đi, đồng thời cân bằng số điểm 12 và vượt lên dẫn đầu bảng H. Nếu thắng Zenit trong trận cuối, nhà đương kim vô địch sẽ vào vòng 1/8 với tư cách nhất bảng.",ngaydang=DateTime.Parse("29/09/2022"),nguoidang="Quản trị",hinhanh="h3.jpg",tieudehinh="Các cầu thủ Juventus phản ứng dữ dội, sau khi Chalobah ghi bàn từ tình huống để bóng chạm tay trước đó của Rudiger.",luotxem=0,noibat=true,kichhoat=true},
				new bantin{chudeid=6,tieude="Thủ tướng Kishida: Quan hệ Việt - Nhật đang tốt đẹp nhất",tomtat="Thủ tướng Kishida Fumio đánh giá cao quan hệ tốt đẹp nhất trong lịch sử giữa Việt Nam và Nhật Bản khi hội đàm với Thủ tướng Phạm Minh Chính.",noidung="Trong cuộc hội đàm với Thủ tướng Phạm Minh Chính tối nay ở Tokyo, Thủ tướng Kishida Fumio cũng khẳng định quan hệ hai nước đã phát triển vượt bậc những năm gần đây. Ông đánh giá cao thành tựu phát triển kinh tế, xã hội cũng như vị thế, vai trò ngày càng quan trọng của Việt Nam ở khu vực và trên thế giới. Thủ tướng Phạm Minh Chính nhắc lại Việt Nam coi Nhật Bản là đối tác chiến lược tin cậy, quan trọng và lâu dài. Ông nêu một số đề xuất thúc đẩy hợp tác song phương, như tăng kết nối kinh tế, hợp tác đầu tư, thương mại, xây dựng cơ sở hạ tầng, biến đổi khí hậu. Hai Thủ tướng nhất trí cần tiếp tục thúc đẩy hợp tác an ninh, quốc phòng, đặc biệt trong lĩnh vực chuyển giao công nghệ trang bị quốc phòng, quân y, an ninh mạng, tham gia gìn giữ hòa bình Liên Hợp Quốc, khắc phục hậu quả chiến tranh tại Việt Nam, thúc đẩy hợp tác nâng cao năng lực thực thi pháp luật trên biển, xem xét mở lại đường bay thương mại, triển khai hộ chiếu vaccine. Việt Nam - Nhật Bản thiết lập quan hệ ngoại giao từ năm 1973. Nhật Bản là đối tác kinh tế quan trọng hàng đầu của Việt Nam và là nước G7 đầu tiên công nhận quy chế kinh tế thị trường của Việt Nam vào năm 2011. Nhật là nước tài trợ ODA lớn nhất, nhà đầu tư số hai và đối tác thương mại lớn thứ tư của Việt Nam.",ngaydang=DateTime.Parse("29/09/2022"),nguoidang="Quản trị",hinhanh="h4.jpg",tieudehinh="Thủ tướng Phạm Minh Chính và Thủ tướng Kishida Fumio tại Văn phòng Thủ tướng Nhật Bản tối 24/11.",luotxem=0,noibat=true,kichhoat=true},
				new bantin{chudeid=6,tieude="Tổng bí thư: Văn hóa còn thì dân tộc còn",tomtat="Nhắc đến câu nói của một tiền bối, Tổng bí thư Nguyễn Phú Trọng chia sẻ quan điểm 'văn hóa là bản sắc của dân tộc, văn hóa còn thì dân tộc còn, văn hóa mất thì dân tộc mất'.",noidung="Hội nghị Văn hóa toàn quốc triển khai Nghị quyết Đại hội XIII khai mạc sáng 24/11 tại Hà Nội. Tham dự có Tổng bí thư Nguyễn Phú Trọng; nguyên Tổng bí thư Nông Đức Mạnh; Chủ tịch nước Nguyễn Xuân Phúc; Chủ tịch Quốc hội Vương Đình Huệ; Thường trực Ban Bí thư Võ Văn Thưởng... Hơn một tiếng phát biểu, Tổng bí thư Nguyễn Phú Trọng nhiều lần trích đọc các bài thơ nổi tiếng, được nhiều thế hệ yêu thích, như Chân quê (Nguyễn Bính); Việt Bắc (Tố Hữu)... Sau mỗi lần ông đọc thơ, các đại biểu đồng loạt vỗ tay hưởng ứng. Sau khi nhắc đến câu nói của Chủ tịch Hồ Chí Minh 'văn hóa soi đường cho quốc dân đi', Tổng bí thư chia sẻ thêm 'tôi nhớ trước đây, một vị tiền bối từng nói văn hóa là bản sắc của một dân tộc. Văn hóa còn thì dân tộc còn. Văn hóa mất thì dân tộc mất'.Sau khi nhắc đến câu nói của Chủ tịch Hồ Chí Minh 'văn hóa soi đường cho quốc dân đi', Tổng bí thư chia sẻ thêm 'tôi nhớ trước đây, một vị tiền bối từng nói văn hóa là bản sắc của một dân tộc. Văn hóa còn thì dân tộc còn. Văn hóa mất thì dân tộc mất'. Để chấn hưng và phát triển nền văn hóa dân tộc, Tổng bí thư Nguyễn Phú Trọng đề nghị tập trung thực hiện tốt một số nhiệm vụ trọng tâm, như: Khơi dậy mạnh mẽ hơn nữa tinh thần yêu nước, ý chí tự cường, tinh thần đoàn kết, khát vọng phát triển đất nước phồn vinh, hạnh phúc...",ngaydang=DateTime.Parse("29/09/2022"),nguoidang="Quản trị",hinhanh="h5.jpg",tieudehinh="Tổng bí thư Nguyễn Phú Trọng trò chuyện cùng các đại biểu dự Hội nghị văn hóa toàn quốc, sáng 24/11.",luotxem=0,noibat=true,kichhoat=true},
				new bantin{chudeid=6,tieude="F0 tử vong cao ở nhóm nguy cơ, TP HCM kêu gọi tiêm vaccine",tomtat="F0 tử vong thời gian qua chủ yếu ở nhóm nguy cơ cao và chưa tiêm vaccine, Sở Y tế TP HCM kêu gọi người tranh thủ đi tiêm vét.",noidung="Chiều 13/12, tại họp báo về công tác phòng chống dịch, Chánh văn phòng Sở Y tế Nguyễn Thị Huỳnh Mai cho biết nhờ chiến dịch bảo vệ người thuộc nhóm nguy cơ cao (trên 65 tuổi, có bệnh nền), thành phố đã tìm được nguyên nhân số ca tử vong dai dẳng nhiều tháng qua. Đó là những người trên 65 tuổi, có bệnh nền và chưa tiêm vaccine. Nhiều gia đình, ông bà, cha mẹ lớn tuổi, kèm bệnh lý nền nghiêm trọng, như tai biến mạch máu não nằm liệt giường nhưng sợ tiêm vaccine sẽ có biến chứng nguy hiểm. Nhiều người sẵn sàng ký vào giấy không tiêm vaccine. Trong khi đó, những người lớn tuổi này có thể bị lây từ chính người thân của mình, khi người nhà tiếp xúc ở ngoài mang theo mầm bệnh về nhà. Ngành y tế TP HCM đang dồn lực can thiệp ở nhóm nguy cơ cao, lập danh sách người trên 65 tuổi, có bệnh nền, lấy mẫu xét nghiệm Covid-19. Trường hợp dương tính được cấp phát các túi thuốc A (hạ sốt và vitamin), B (kháng viêm, chống đông), C (thuốc kháng virus molnupiravir). Mạng lưới Thầy thuốc đồng hành và trạm y tế lưu động phân công chăm sóc hàng ngày cho nhóm này, chuyển viện ngay khi trở nặng, giảm tỷ lệ tử vong. Người thuộc nhóm nguy cơ chưa tiêm hoặc tiêm chưa đủ liều, nên tiêm vét hoặc sẽ được đội lưu động đến tận nhà tiêm cho người không thể đi lại.",ngaydang=DateTime.Now,nguoidang="Quản trị",hinhanh="h6.jpg",tieudehinh="Tiêm mũi vaccine Covid-19 bổ sung tại Gò Vấp, sáng 10/12",luotxem=0,noibat=true,kichhoat=true},
				new bantin{chudeid=1,tieude="Nhà sáng lập Sandro: Chuẩn mực là của bạn",tomtat="Nhờ tinh thần giữ vững định hướng chuẩn mực thẩm mỹ, Evelyne Chetrite - nhà sáng lập Sandro đã tạo nên thương hiệu thời trang với hơn 700 cửa hàng tại 39 quốc gia.",noidung="Tại Việt Nam, thương hiệu sẽ mở cửa hàng thứ hai tại Hà Nội và trang mua sắm trực tuyến trong tháng 12. Với hơn 37 năm phát triển, thương hiệu Sandro là toàn bộ chiêm nghiệm, triết lý thời trang của nhà sáng lập - Evelyne Chetrite. Xuất phát từ lăng kính của một cô bé người Pháp, Sandro được xây dựng hình ảnh bằng tình yêu dành cho đường phố, kiến trúc và những đô thị rực rỡ sắc màu. Tại Việt Nam, Sandro được phân phối chính hãng bởi Công ty Quốc tế Tam Sơn. Bên cạnh việc khai trương cửa hàng đầu tiên tại Hà Nội, Sandro cũng ra mắt trang mua sắm trực tuyến từ ngày 10/12, cho phép người yêu thời trang trong nước có thể lựa chọn những sản phẩm mới nhất từ thương hiệu này an toàn và thuận tiện hơn. Evelyne Chetrite - nhà sáng lập Sandro tin rằng phong cách Paris đến từ sự thanh lịch, trầm lặng, nơi một bộ trang phục vừa vặn, cổ điển và thông minh sẽ lên tiếng. Một chi tiết nguyên bản được biến tấu duyên dáng như một chiếc cài áo hay vòng cổ cũng sẽ rất phù hợp với mọi cô gái. Mỗi người nên làm theo sở thích của mình và sống thật với chính mình, dù là trong thời trang hay cuộc sống.",ngaydang=DateTime.Now,nguoidang="Quản trị",hinhanh="h7.jpg",tieudehinh="Sandro sẽ mở cửa hàng thứ hai tại Hà Nội và trang mua sắm trực tuyến trong tháng 12.",luotxem=0,noibat=true,kichhoat=true}
			};


